INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('e8a76741-7533-4c76-a9ad-a45f1e0a5685', 'Birds of a Feather', 'birds of a feather', 'http://www.phish.net/song/birds-of-a-feather/lyrics', '       it''s easy sometimes when you just coast along but like it or not something always seems to go wrong sometimes people build you up just so they can knock you down sometimes they will have you there ''cause they need someone around  perhaps you''ll receive invitations for tea or perhaps you will laugh and you will make them all smile or maybe you''ll join them ''cause it had to be perhaps you''ll forget you forgot for a while  birds of a feather are flocking outside like whippets they dance in a curly-queue dance of pulses and ringing and campfire chants of ritual drumming although at first glance you thought you could run but you won''t take a chance  it''s not an experience if they can''t bring someone along they hang on emotions they bottle inside they peck at the ground  and they strut out of stride  ', null);
INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('c70291a9-d6e9-4dcf-bb23-ecfa05211522', 'Meat', 'meat', 'http://www.phish.net/song/meat/lyrics', '      i am a prince i have it all i hear your footsteps through the wall i wait in silence for your call then take a shot and watch you fall  i am a ghost but i cannot fly i''m stuck here as the years slide by i need a resting place ''cause i already felt my body die  if i had a host of ghosts living on my street i''d jive and strive to stay alive and offer them some meat  i just felt like my heart stopped beating  you just thought that you heard me laughing  i jumped up from the sofa under-cover  why''d you put the pillow on it?  i need a different life i think perhaps i''d be the missing link and treasure moments as i drink away the memories let them sink  ', null);
INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('de6e382f-5fd8-4eff-82b4-579bc1ecd465', 'Guyute', 'guyute', 'http://www.phish.net/song/guyute/lyrics', '      guyute was the ugly pig who walked on me and danced a jig that he had learned when he was six then stopped and did some other tricks  like pulling weapons from his coat and holding them against my throat he lectured me in a language strange then scampered quickly out of range  i''m bouncing like a new born elf i can''t believe inside myself guyute glances in my eyes and manages to hypnotize  me as i sleep the sleep of death he sucks from me my only breath that i had breathed since i was ten i hope this happens once again  ', null);
INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('50be48af-a85b-4f1a-bd61-36c4e42993dd', 'Fikus', 'fikus', 'http://www.phish.net/song/fikus/lyrics', '      fikus dreamed a dream for me it cost me nothing it was free he dreamed of walking in the sand of blossoms forming in his hand  of kitchen cake and cedar shakes of waterfalls and yellow snakes of earthen dams and hydrofoils of watching water ''til it boils  of holding forth from singing words of flying south with flocks of birds none of this was charged to me for as i said it was for free  ', null);
