INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('94882904-f14c-43ed-a2ca-62bab24f1f32', 'Wading in the Velvet Sea', 'wading in the velvet sea', 'http://www.phish.net/song/wading-in-the-velvet-sea/lyrics', '      i took a moment from my day wrapped it up in things you say mailed it off to your address you''ll get it pretty soon soon unless  the packaging begins to break and all the points i tried to make are tossed like thoughts into a bin as time leaks out, my life leaks in  you won’t find moments in a box and someone else will set your clocks i took a moment from my day and wrapped it up in things you say and mailed it off to you  i''ve been wading in the velvet sea i''ve been wading in the velvet sea i''ve been wading in the velvet sea i''ve been wading in the, i''ve been wading in the... i''ve been wading in the velvet sea  ', null);
