INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('362b9c7c-0b40-44a1-83fc-b38e053ca81e', 'Dirt', 'dirt', 'http://www.phish.net/song/dirt/lyrics', '  (anastasio, marshall, herman)    i''d like to live beneath the dirt a tiny space to move and breathe is all that i would ever need  i wanna live beneath the dirt where i''d be free from push and shove like all those swarming up above  beneath their heels i''ll spend my time i''ll wriggle in the earth and dew and sometimes i will think of you  and if you ever think of me kneel down and kiss the earth and show me what this thought is worth  ', 'anastasio, marshall, herman');
