INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('f7ca9ff6-2374-486a-a499-4dc3e5772a43', 'Mock Song', 'mock song', 'http://www.phish.net/song/mock-song/lyrics', '      zither, rachel, cardamom, frost - but at what cost? threshold, skin, fortress, win - a life of sin pliers, tension, toy store, grace - the human race raking, fourteen, basket, screw - now how ''bout you? passion, cardboard, raisin, stop - a better crop field, cater, open, new - who''s mocking who? it''s just a mock song...  gas, thirsty, rattle, tick - a bone to pick fountain, egg, treason, cave - will you dance on grave? throat, carrot, meeting, choke - i got that joke temper, thistle, rodney, wilt - the house i built fewer, pink, kyle, ball - i''ve known them all penny, thistle, cell phone, blow - reap what you sow because it''s just a mock song call it what you will.  ', null);
INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('ff14d07d-8d85-4697-8685-a767559e7068', '46 Days', '46 days', 'http://www.phish.net/song/46-days/lyrics', '      leigh fordham sold me out 46 days and the coal ran out please come round here another day sit yourself down when you''re ready to stay  she dug down when they took the town lookin'' for clues but they couldn''t be found leigh found out she was ready to roam 47 days and the coal came home  taste the fear  for the devil''s drawing near  ', null);
INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('e1fc918f-0660-41b7-8249-064be5648ded', 'All of These Dreams', 'all of these dreams', 'http://www.phish.net/song/all-of-these-dreams/lyrics', '      there is a place on the mountain nearby deep in a cave  but it''s up rather high there in the darkness are safely concealed all of the dreams  that you never revealed  and if you go there and after you do all of these dreams  would be yours to pursue the rest of your lifetime  devoid of a care if you keep your eyes open  you may find yourself there  such is the promise such is the curse you could just live your life  better or worse knowing the cache of dreams  up on that hill beckons and sways  but won''t bend to your will  you might find a river  under a mountain that feeds a remote subterranean fountain  drink from this taste  just a hint of a dream that somehow leaked into  the underground stream  ', null);
INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('7b7395a1-2049-4c50-84b3-0a6b72db5139', 'Walls of the Cave', 'walls of the cave', 'http://www.phish.net/song/walls-of-the-cave/lyrics', '      i''m leaving you a message i''m leaving you a trace i''m leaving thoughts for you i hope that time will not erase and when the moment comes  to read the words that i engrave you''ll find them on the walls of the cave  i know you heard the question but you didn''t make a sound and when it fell you caught my heart before it hit the ground but if you ever need the names  of those you couldn''t save you''ll find them on the walls of the cave  look at me the time stands still the mountain here is now a hill look away the time goes past these rocks will fall away at last  maybe you will see it as you''re passing by alone below the moss forgotten where some words adorn a stone it might have been an etching on a marker of a grave or maybe on the walls of the cave  listen to the silent trees but still your words  float on the breeze look away i see them all carved into the cavern wall  ', null);
INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('d6e4e41d-7627-4fbb-9321-3c43d03cd5cb', 'Thunderhead', 'thunderhead', 'http://www.phish.net/song/thunderhead/lyrics', '      i need a new way to express myself so you don''t need to guess what otherwise i''d say aloud and watch it float just like a cloud  high up above you like a thunderhead but you would just look down instead and wait ''til foggy skies abate and vapor clouds all dissipate  and when with them all my words for you have softly sublimated too and you''ll just hope that i''ve moved on so you''ll look up and find me gone  ', null);
INSERT INTO song_entity (id, name, name_lower, link, lyrics, lyrics_by) VALUES ('2c04905e-dd62-4ce7-afba-634590c6660c', 'Waves', 'waves', 'http://www.phish.net/song/waves/lyrics', '       how is it i never see the waves that bring her words to me for though unseen they drift around they catch my breath and knock me down  i feel them quickly swirling by as they withdraw with my reply and slip into the dark of night as i attempt to stay upright  instead i''m stranded on my knees as words depart upon the breeze that quickly drifts away from here and possibly won''t reach her ear  if we could see these many waves that flow through clouds and sunken caves she''d sense at least the words that sought her on the wind and underwater  ', null);
